import React, { useState, useEffect, useRef, useCallback } from "react";
import { useParams, Navigate, useNavigate } from "react-router-dom";
import { confirmAlert } from "react-confirm-alert";
import "react-confirm-alert/src/react-confirm-alert.css";

import Auth from "../utils/auth";
import {
  getCardioById,
  getResistanceById,
  updateCardio,
  updateResistance,
  deleteCardio,
  deleteResistance,
} from "../utils/API";
import { formatDate } from "../utils/dateFormat";
import Header from "./Header";
import cardioIcon from "../assets/images/cardio-w.png";
import resistanceIcon from "../assets/images/resistance-w.png";

export default function SingleExercise() {
  const { id, type } = useParams();
  const [cardioData, setCardioData] = useState({});
  const [resistanceData, setResistanceData] = useState({});
  const [isUpdating, setIsUpdating] = useState(false); // State to track whether updating mode is active

  const updatedNameRef = useRef(null);
  const updatedDistanceRef = useRef(null);
  const updatedDurationRef = useRef(null);
  const updatedWeightRef = useRef(null);
  const updatedSetsRef = useRef(null);
  const updatedRepsRef = useRef(null);

  const loggedIn = Auth.loggedIn();
  const navigate = useNavigate();

  const displayExercise = useCallback(
    async (exerciseId) => {
      // get token
      const token = loggedIn ? Auth.getToken() : null;
      if (!token) return false;

      // fetch cardio data by id
      if (type === "cardio") {
        try {
          const response = await getCardioById(exerciseId, token);
          if (!response.ok) {
            throw new Error("something went wrong!");
          }

          const cardio = await response.json();
          cardio.date = formatDate(cardio.date);
          setCardioData(cardio);
        } catch (err) {
          console.error(err);
        }
      }

      // fetch resistance data by id
      else if (type === "resistance") {
        try {
          const response = await getResistanceById(exerciseId, token);
          if (!response.ok) {
            throw new Error("something went wrong!");
          }

          const resistance = await response.json();
          resistance.date = formatDate(resistance.date);
          setResistanceData(resistance);
        } catch (err) {
          console.error(err);
        }
      }
    },
    [type, loggedIn]
  );

  useEffect(() => {
    displayExercise(id);
  }, [id, displayExercise]); // Make sure to close the array with "]"

  if (!loggedIn) {
    return <Navigate to="/login" />;
  }

  const handleDeleteExercise = async (exerciseId) => {
    const token = loggedIn ? Auth.getToken() : null;
    if (!token) return false;

    confirmAlert({
      title: "Delete Exercise",
      message: "Are you sure you want to delete this exercise?",
      buttons: [
        {
          label: "Cancel",
        },
        {
          label: "Delete",
          onClick: async () => {
            // delete cardio data
            if (type === "cardio") {
              try {
                const response = await deleteCardio(exerciseId, token);
                if (!response.ok) {
                  throw new Error("something went wrong!");
                }
              } catch (err) {
                console.error(err);
              }
            }

            // delete resistance data
            else if (type === "resistance") {
              try {
                const response = await deleteResistance(exerciseId, token);
                if (!response.ok) {
                  throw new Error("something went wrong!");
                }
              } catch (err) {
                console.error(err);
              }
            }

            // go back to history
            navigate("/history");
          },
        },
      ],
    });
  };

  // Assuming you have a function to get updated data from a form
  const getUpdatedExerciseData = () => {
    let updatedData = {};

    if (type === "cardio") {
      updatedData = {
        name: updatedNameRef.current.value,
        distance: updatedDistanceRef.current.value,
        duration: updatedDurationRef.current.value,
        // Add other cardio properties as needed
      };
    } else if (type === "resistance") {
      updatedData = {
        name: updatedNameRef.current.value,
        weight: updatedWeightRef.current.value,
        sets: updatedSetsRef.current.value,
        reps: updatedRepsRef.current.value,
        // Add other resistance properties as needed
      };
    }

    return updatedData;
  };

  // handle update
  const handleUpdateExercise = async () => {
    const token = loggedIn ? Auth.getToken() : null;
    if (!token) return false;
    // Assuming you have a function to fetch updated data from a form or some other source
    const updatedExerciseData = getUpdatedExerciseData();

    try {
      if (type === "cardio") {
        const response = await updateCardio(id, updatedExerciseData, token);
        if (!response.ok) {
          throw new Error("something went wrong!");
        }
      } else if (type === "resistance") {
        const response = await updateResistance(id, updatedExerciseData, token);
        if (!response.ok) {
          throw new Error("something went wrong!");
        }
      }

      // Fetch and display the updated exercise data
      displayExercise(id);
      // Disable the updating mode after successful update
      setIsUpdating(false);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className={type === "cardio" ? "single-cardio" : "single-resistance"}>
      <Header />
      <h2 className="title text-center">History</h2>

      <div className={type === "cardio" ? "single-cardio" : "single-resistance"}>
        {/* Specific JSX for Cardio or Resistance */}
        {type === "cardio" && (
          <div className="cardio-div">
            <div className="d-flex justify-content-center">
              <img alt="cardio" src={cardioIcon} className="exercise-form-icon" />
            </div>
            <p>
              <span>Date: </span> {cardioData.date}
            </p>
            <p>
              <span>Name: </span> {cardioData.name}
            </p>
            <p>
              <span>Distance: </span> {cardioData.distance} miles
            </p>
            <p>
              <span>Duration: </span> {cardioData.duration} minutes
            </p>
            {isUpdating && (
              <>
                <input type="text" id="updatedName" ref={updatedNameRef} />
                <input type="text" id="updatedDistance" ref={updatedDistanceRef} />
                <input type="text" id="updatedDuration" ref={updatedDurationRef} />
              </>
            )}
            <button className="delete-btn" onClick={() => handleDeleteExercise(id)}>
              Delete Exercise
            </button>
            <button className="update-btn" onClick={() => setIsUpdating(true)}>
              Update Exercise
            </button>
          </div>
        )}

        {type === "resistance" && (
          <div className="resistance-div">
            <div className="d-flex justify-content-center">
              <img alt="resistance" src={resistanceIcon} className="exercise-form-icon" />
            </div>
            <p>
              <span>Date: </span> {resistanceData.date}
            </p>
            <p>
              <span>Name: </span> {resistanceData.name}
            </p>
            <p>
              <span>Weight: </span> {resistanceData.weight} lbs
            </p>
            <p>
              <span>Sets: </span> {resistanceData.sets}
            </p>
            <p>
              <span>Reps: </span> {resistanceData.reps}
            </p>
            {isUpdating && (
              <>
                <input type="text" id="updatedName" ref={updatedNameRef} />
                <input type="text" id="updatedWeight" ref={updatedWeightRef} />
                <input type="text" id="updatedSets" ref={updatedSetsRef} />
                <input type="text" id="updatedReps" ref={updatedRepsRef} />
              </>
            )}
            <button className="delete-btn" onClick={() => handleDeleteExercise(id)}>
              Delete Exercise
            </button>
            <button className="update-btn" onClick={() => setIsUpdating(true)}>
              Update Exercise
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
