import React, { useState, useEffect, useCallback } from 'react';
import { useParams, Navigate, useNavigate } from 'react-router-dom';
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';

import Auth from '../utils/auth';
import {
  getCardioById,
  getResistanceById,
  updateCardio,
  updateResistance,
  deleteCardio,
  deleteResistance,
} from '../utils/API';
import { formatDate } from '../utils/dateFormat';
import Header from './Header';
import cardioIcon from '../assets/images/cardio-w.png';
import resistanceIcon from '../assets/images/resistance-w.png';

export default function SingleExercise() {
  const { id, type } = useParams();
  const [cardioData, setCardioData] = useState({});
  const [resistanceData, setResistanceData] = useState({});
  const [updatedName, setUpdatedName] = useState('');
  const [updatedDistance, setUpdatedDistance] = useState('');
  const [updatedDuration, setUpdatedDuration] = useState('');
  const [updatedWeight, setUpdatedWeight] = useState('');
  const [updatedSets, setUpdatedSets] = useState('');
  const [updatedReps, setUpdatedReps] = useState('');
  const [isUpdating, setIsUpdating] = useState(false); // State to track whether updating mode is active

  const loggedIn = Auth.loggedIn();
  const navigate = useNavigate();

  const displayExercise = useCallback(async (exerciseId) => {
    // get token
    const token = loggedIn ? Auth.getToken() : null;
    if (!token) return false;

    // fetch cardio or resistance data by id
    try {
      const response =
        type === 'cardio'
          ? await getCardioById(exerciseId, token)
          : await getResistanceById(exerciseId, token);

      if (!response.ok) {
        throw new Error('something went wrong!');
      }

      const exerciseData = await response.json();
      exerciseData.date = formatDate(exerciseData.date);

      // Set the exercise data based on the type
      if (type === 'cardio') {
        setCardioData(exerciseData);
      } else {
        setResistanceData(exerciseData);
      }
    } catch (err) {
      console.error(err);
    }
  }, [type, loggedIn]);

  useEffect(() => {
    displayExercise(id);
  }, [id, displayExercise]);

  if (!loggedIn) {
    return <Navigate to="/login" />;
  }

  const handleDeleteExercise = async (exerciseId) => {
    const token = loggedIn ? Auth.getToken() : null;
    if (!token) return false;

    confirmAlert({
      title: 'Delete Exercise',
      message: 'Are you sure you want to delete this exercise?',
      buttons: [
        {
          label: 'Cancel',
        },
        {
          label: 'Delete',
          onClick: async () => {
            // delete cardio or resistance data
            try {
              if (type === 'cardio') {
                const response = await deleteCardio(exerciseId, token);
                if (!response.ok) {
                  throw new Error('something went wrong!');
                }
              } else {
                const response = await deleteResistance(exerciseId, token);
                if (!response.ok) {
                  throw new Error('something went wrong!');
                }
              }

              // go back to history
              navigate('/history');
            } catch (err) {
              console.error(err);
            }
          },
        },
      ],
    });
  };

  const handleUpdateExercise = async () => {
    const token = loggedIn ? Auth.getToken() : null;
    if (!token) return false;

    // Prepare updated data based on the type
    const updatedData =
      type === 'cardio'
        ? {
            name: updatedName,
            distance: updatedDistance,
            duration: updatedDuration,
          }
        : {
            name: updatedName,
            weight: updatedWeight,
            sets: updatedSets,
            reps: updatedReps,
          };

    try {
      // Update cardio or resistance data
      if (type === 'cardio') {
        const response = await updateCardio(id, updatedData, token);
        if (!response.ok) {
          throw new Error('something went wrong!');
        }
      } else {
        const response = await updateResistance(id, updatedData, token);
        if (!response.ok) {
          throw new Error('something went wrong!');
        }
      }

      // Fetch and display the updated exercise data
      displayExercise(id);

      // Disable the updating mode after successful update
      setIsUpdating(false);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className={type === 'cardio' ? 'single-cardio' : 'single-resistance'}>
      <Header />
      <h2 className="title text-center">History</h2>
      <div className="single-exercise d-flex flex-column align-items-center text-center">
        {type === 'cardio' && (
          <div className="cardio-div ">
            <div className="d-flex justify-content-center">
              <img alt="cardio" src={cardioIcon} className="exercise-form-icon" />
            </div>
            <p>
              <span>Date: </span> {cardioData.date}
            </p>
            <p>
              <span>Name: </span> {cardioData.name}
            </p>
            <p>
              <span>Distance: </span> {cardioData.distance} miles
            </p>
            <p>
              <span>Duration: </span> {cardioData.duration} minutes
            </p>
            {isUpdating && (
              <>
              <label>name</label>
                <input
                  type="text"
                  id="updatedName"
                  value={updatedName}
                  onChange={(e) => setUpdatedName(e.target.value)}
                />
                 <label>distance</label>
                <input
                  type="text"
                  id="updatedDistance"
                  value={updatedDistance}
                  onChange={(e) => setUpdatedDistance(e.target.value)}
                />
                 <label>duration</label>
                <input
                  type="text"
                  id="updatedDuration"
                  value={updatedDuration}
                  onChange={(e) => setUpdatedDuration(e.target.value)}
                />
              </>
            )}
            <button className="delete-btn" onClick={() => handleDeleteExercise(id)}>
              Delete Exercise
            </button>
            <button className="update-btn" onClick={() => setIsUpdating(true)}>
              Update Exercise
            </button>
          </div>
        )}
        {type === 'resistance' && (
          <div className="resistance-div">
            <div className="d-flex justify-content-center">
              <img alt="resistance" src={resistanceIcon} className="exercise-form-icon" />
            </div>
            <p>
              <span>Date: </span> {resistanceData.date}
            </p>
            <p>
              <span>Name: </span> {resistanceData.name}
            </p>
            <p>
              <span>Weight: </span> {resistanceData.weight} lbs
            </p>
            <p>
              <span>Sets: </span> {resistanceData.sets}
            </p>
            <p>
              <span>Reps: </span> {resistanceData.reps}
            </p>
            {isUpdating && (
              <>
                <input
                  type="text"
                  id="updatedName"
                  value={updatedName}
                  onChange={(e) => setUpdatedName(e.target.value)}
                />
                <input
                  type="text"
                  id="updatedWeight"
                  value={updatedWeight}
                  onChange={(e) => setUpdatedWeight(e.target.value)}
                />
                <input
                  type="text"
                  id="updatedSets"
                  value={updatedSets}
                  onChange={(e) => setUpdatedSets(e.target.value)}
                />
                <input
                  type="text"
                  id="updatedReps"
                  value={updatedReps}
                  onChange={(e) => setUpdatedReps(e.target.value)}
                />
              </>
            )}
            <button className="delete-btn" onClick={() => handleDeleteExercise(id)}>
              Delete Exercise
            </button>
            <button className="update-btn" onClick={() => setIsUpdating(true)}>
              Update Exercise
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
